import React, { Component } from 'react'
import { connect } from 'react-redux';
import { _fetchCountries } from '../actions/index';


class SearchBar extends Component {

componentWillMount(){
    this.props._fetchCountries();
}


renderSelectCountries=()=>{

    return(
        <select>
            {this.props.countries.map(country =>{
                <option keys={country.code} value={country.code} >
                    {country.name}
                </option>
            })}
         </select>
    )
}




render () {
    console.log(this.props.countries)
    return (
         <form>
             {this.renderSelectCountries() }
        </form>
        )
    }
}




const  mapStateToProps = state => ({

    countries: state.countries.countries
})

export default connect(mapStateToProps, {_fetchCountries})(SearchBar);
