import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
//1
import ReactChartkick, { AreaChart } from "react-chartkick";
import Chart from "chart.js";
import { formatedDate } from '../utils/func';


//2
ReactChartkick.addAdapter(Chart);
export default class RateExchangeItem extends Component {
    static propTypes = {
        rateExchange: PropTypes.object,
    }

    render() {

        const { name, flag, rates } = this.props.rateExchange
        console.log(name)

        return (
        <Fragment>
                <tr>
                    <td>{name}<br /><img src={flag} alt={name} height="60" width="100" /></td>
                    <td className="col-md-12">
                        <AreaChart 
                            data={formatedDate(rates, currency)} 
                            xtitle="Date" 
                            ytitle="" /></td>
                </tr>    
        </Fragment>
        )
    }
}
