export const formatedDate=(date)=>{
   // console.log('dafault',date)       // default
   // console.log('toString',date.toString()); //output: Wed Oct 05 2011 16:48:00 GMT+0200 (CEST)
    console.log("split", date.toString().split("T")[0]);

    return date.toISOString().split("T")[0]; // output: 2011-10-05T14:48:00.000Z

}


/**
 * Split  => permet de diviser une chaîne de caractères
 * à partir d'un séparateur pour fournir un tableau de sous-chaînes.
 */


 export const getLastMonth=()=>{
     let now = new Date();
     let last_date = now.setMonth(now.getMonth() - 1)
     return formatedDate(last_date) ;
 }
