import axios from 'axios'
//Types 
import { GET_COUNTRIES } from "./types";


export const _fetchCountries= ()=> async dispatch =>{


   const response = await axios.get('https://restcountries.eu/rest/v2/all');

        dispatch({
            type: GET_COUNTRIES,
            payload:response.data
        });
};


export const _fetchRateExchange = (country)=> async dispatch =>{

    const response = await axios.get(`https://api.exchangeratesapi.io/history?start_at=2018-01-01&end_at=2018-09-01&base=USD&symbols=${country.code}`);
    console.log(response)
    dispatch({
      type: GET_RATE_EXCHANGE,
      payload: { rates: response.data.rates,  }
    });


}
