import axios from 'axios'
//Types 
import { GET_COUNTRIES, GET_RATE_EXCHANGE } from "./types";
import { formatedDate, getLastMonth } from '../utils/func';


export const _fetchCountries= ()=> async dispatch =>{


   const response = await axios.get('https://restcountries.eu/rest/v2/all');

        dispatch({
            type: GET_COUNTRIES,
            payload:response.data
        });
};


export const _fetchRateExchange = (country)=> async dispatch =>{

    const response = await axios.get(`https://api.exchangeratesapi.io/history?start_at=${getLastMonth() }&end_at=${formateate( new Date() )}&base=USD&symbols=${country.code}`);
    console.log('rate',response)
    console.log("date", formatedDate(new Date()));


    dispatch({
      type: GET_RATE_EXCHANGE,
      payload: { rates: response.data.rates, ...country }
    });


};

function formatedae(date){

    return date.toISOString().split('T')[0]
}
